const horariosImportantes = [6, 9, 12, 15, 18];

const hoje = new Date().toLocaleDateString("pt-BR", {
    timeZone: "America/Bahia"
});

const lista = $input.first().json.list;

const climaAbrantesfiltrado = lista
    .filter(item => {
        const data = new Date(item.dt * 1000);

        const dataLocal = data.toLocaleDateString("pt-BR", {
          timeZone: "America/Bahia"
        });

        const hora = Number(
            data.toLocaleString("en-US", {
                timeZone: "America/Bahia",
                hour: "2-digit",
                hour12: false
            })
        );

        return dataLocal === hoje && horariosImportantes.includes(hora);
    })
    .map(item => {

        const probabilidade = item.pop ?? 0;
        const vento = item.wind?.speed ?? 0;
        const rajada = item.wind?.gust ?? 0;

        return {
            horario: new Date(item.dt * 1000).toLocaleString("en-US", {
                timeZone: "America/Bahia",
                hour: "2-digit",
                hour12: false
            }),
            temperatura: item.main?.temp ?? 0,
            sensacao: item.main?.feels_like ?? 0,
            probabilidadeChuva: probabilidade,
            clima: item.weather?.[0]?.main ?? "Desconhecido",
            descricao: item.weather?.[0]?.description ?? "",
            vento: vento,
            rajada: rajada
        };
    });

return [{
    climaAbrantesfiltrado
}];

